import React, { useState } from 'react';
import styled from 'styled-components'
import Header from '../../components/Header'
import { useHistory } from 'react-router-dom';

const Formbox = styled.div`
    width: 70vw;
    height: 45vh;
    margin-left: 15vw;
    margin-top: 15vh;
    opacity: 0.85;
    background-color: purple;
    text-align: center;

            /**************************************/
            body {
            padding: 1rem;
            }

            .wrapper {
            box-shadow: 0 0 5px 0 rgba(0, 0, 0, 0.2);
            }
            .wrapper > * {
            padding: 1em;
            }

            ul {
            list-style: none;
            padding: 0;
            }

            .contacts {
            background: #4b5195;
            color: #fff;
            }

            .form {
            background: #fff;
            }

            form label {
            display: block;
            }

            button,
            input,
            textarea {
            padding: 1em;
            }

            button {
            background: lightgrey;
            width: 100%;
            border: 0;
            }
            button:hover, button:focus {
            background: gold;
            outline: 0;
            }

            .contain {
            width: 800px;
            margin-left: auto;
            margin-right: auto;
            padding: 1em;
            }
            @media (min-width: 600px) {
            .contain {
                padding: 0;
            }
            }

            h3,
            ul {
            margin: 0;
            }

            h3 {
            margin-bottom: 1rem;
            }

            input:focus,
            textarea:focus {
            outline: 3px solid gold;
            }

            input,
            textarea,
            button {
            width: 100%;
            border: 1px solid #000;
            }
`
const Page = styled.div`
    background-image: url('https://www.tokiomarine.com.br/vidasaudavel/wp-content/uploads/sites/6/2019/09/GettyImages-1047643090.jpg');
    min-height: 100vh;
`
export default function Home(){
    const [nome, setNome] = useState('')
    const [idade, setIdade] = useState(18)

    const history = useHistory()

    return(
        <Page>
            <Header />
            <Formbox>
                <div>
                    <input placeholder='Nome' onChange={(event) => {setNome(event.target.value)}}/>
                    <input placeholder='Idade' onChange={(event) => {setIdade(event.target.value)}}/>
                    <label for="modalidade">Escolha uma modalidade:</label>
                    <select id="modalidade">
                    <option value="zumba">Zumba</option>
                    <option value="yoga">Yoga</option>
                    <option value="spinning">Spinning</option>
                    <option value="musculacao">Musculação</option>
                    <option value="muaythai">Muay-Thai</option>
                    <option value="bodypump">Body-Pump</option>
                    </select>
                </div>
                <div>
                    <button onClick={() => {
                        console.log(nome, idade);
                        history.push("/results")
                    }}>Submeter</button>
                </div>
            </Formbox>

        </Page>

    );

};
