import React, { useState } from 'react';
import styled from 'styled-components'
import Header from '../../components/Header'
import { useHistory } from 'react-router-dom';
import Background from  '../../images/background.jpg'

const Formbox = styled.div`
    width: 70vw;
    height: 45vh;
    margin-left: 15vw;
    margin-top: 15vh;
    opacity: 0.85;
    background-color: #a60000;
    text-align: center;

    input,
    textarea,
    label,
    select,
    option,
    button {
    padding: 1em;
    margin-top: 2px;
    width: 85%;
    border: 1px solid grey;
    }

    button {
    background: lightgrey;
    }

    button:hover, button:focus {
    background: gold;
    outline: 0;

    }

    input:focus,
    textarea:focus {
    outline: 3px solid gold;
    border: 0;
    }

`
const Page = styled.div`
    background-image: "url(" + Background } + ")";
    min-height: 100vh;
`
export default function Home(){
    const [nome, setNome] = useState('')
    const [idade, setIdade] = useState(18)
    const [email, setEmail] = useState('')
    const [regiao, setRegiao] = useState('')
    const [modalidade, setModalidade] = useState('')

    const history = useHistory()

    return(
        <Page>
            <Header />
            <Formbox>
                <div>
                    <input placeholder='Nome' onChange={(event) => {setNome(event.target.value)}}/>
                    <input placeholder='Idade' onChange={(event) => {setIdade(event.target.value)}}/>
                    <input placeholder='Email' onChange={(event) => {setEmail(event.target.value)}}/>
                    <select id="regiao" onChange={(event) => {setRegiao(event.target.value)}}>
                        <option value="" disabled selected>Escolha sua Região</option>
                        <option value="barra">Barra Funda</option>
                        <option value="republica">República</option>
                        <option value="itaimbibi">Itaim Bibi</option>
                        <option value="belavista">Bela Vista</option>
                        <option value="butanta">Butantã</option>
                        <option value="pinheiros">Pinheiros</option>
                        <option value="paulista">Paulista</option>
                    </select>
                    <select id="modalidade" onChange={(event) => {setModalidade(event.target.value)}}>
                        <option value="" disabled selected>Escolha uma modalidade</option>
                        <option value="zumba">Zumba</option>
                        <option value="yoga">Yoga</option>
                        <option value="spinning">Spinning</option>
                        <option value="musculacao">Musculação</option>
                        <option value="muaythai">Muay-Thai</option>
                        <option value="bodypump">Body-Pump</option>
                        <option value="corrida">Corrida</option>
                    </select>
                </div>
                <div>
                    <button onClick={() => {
                        console.log(nome, idade);
                        window.localStorage.setItem("nome", nome)
                        window.localStorage.setItem("idade", idade)
                        window.localStorage.setItem("email", email)
                        window.localStorage.setItem("regiao", regiao)
                        window.localStorage.setItem("modalidade", modalidade)
                        history.push("/results")
                    }}>Submeter</button>
                </div>
            </Formbox>

        </Page>

    );

};
