import React, { useState } from 'react';
import styled from 'styled-components'
import Header from '../../components/Header'
import { useHistory } from 'react-router-dom';
import AdsAcad from '../../images/academiads.jpg'
import AdsCard from '../../components/AdsCard';

import AdvertisementsService from '../../services/Advertisements';

const Formbox = styled.div`
    width: 70vw;
    height: 8vh;
    margin-left: 15vw;
    margin-top: 15vh;
    opacity: 0.85;
    background-color: purple;


    p {
    margin: auto; /* Important */ 
    text-align: center; 
    font-size: 3rem;
    font-weight: bolder;
    color: #333;
    &.white {
      color: white;
    }
  }
`


const Page = styled.div`
    background-image: url('https://www.tokiomarine.com.br/vidasaudavel/wp-content/uploads/sites/6/2019/09/GettyImages-1047643090.jpg');
    min-height: 100vh;
`

const ad = {
    image: AdsAcad,
    link: 'https://google.com',
    text: 'As melhores academias a UM CLIQUE de distância'
  };


export default function Ordered(){
    const history = useHistory()
    const [advertisements, setAdvertisements] = useState([]);
    useEffect(
        () => {
        AdvertisementsService.getByOwner('happyfit').then(result => {
            setAdvertisements(result.data.advertisements);
        });
        },
        []
    );

    return(
        <Page>
            <Header />
            <Formbox>
                <div>
                    <p className="white">RESERVA REALIZADA!</p>
                </div>
            </Formbox>
            {advertisements.map((advertisement) => {
                return (
                <AdsCard
                    key={advertisement.id}
                    image={advertisement.image}
                    link={advertisement.link}
                    />
            );
      })}


        </Page>

    );

};
