import React, { useState } from 'react';
import styled from 'styled-components'
import Header from '../../components/Header'
import { useHistory } from 'react-router-dom';

const Formbox = styled.div`
    width: 70vw;
    height: 45vh;
    margin-left: 15vw;
    margin-top: 15vh;
    opacity: 0.85;
    background-color: purple;
    text-align: center;
    /* display: grid; */
`
const Page = styled.div`
    background-image: url('https://www.tokiomarine.com.br/vidasaudavel/wp-content/uploads/sites/6/2019/09/GettyImages-1047643090.jpg');
    min-height: 100vh;
`
export default function Home(){
    const [nome, setNome] = useState('')
    const [idade, setIdade] = useState(18)

    const history = useHistory()

    return(
        <Page>
            <Header />
            <Formbox>
                <div>
                    <input placeholder='Nome' onChange={(event) => {setNome(event.target.value)}}/>
                    <input placeholder='Idade' onChange={(event) => {setIdade(event.target.value)}}/>
                    <label for="modalidade">Escolha uma modalidade:</label>
                    <select id="modalidade">
                    <option value="zumba">Zumba</option>
                    <option value="yoga">Saab</option>
                    <option value="mercedes">Mercedes</option>
                    <option value="audi">Audi</option>
                    </select>
                </div>
                <div>
                    <button onClick={() => {
                        console.log(nome, idade);
                        history.push("/results")
                    }}>Submeter</button>
                </div>
            </Formbox>

        </Page>

    );

};
