import React, { useState } from 'react';
import ReactDOM from 'react-dom';
import { useHistory } from 'react-router-dom';
import styled from 'styled-components';
import HttpService from "../../services/http"


const SCProductCard = styled.div`
  margin: 40px auto;
  width: 70vw;
  height: 300px;
  background-color: white;
  display: grid;
  grid-template-columns: 3fr 3fr 2fr;
  background-color: #333;

  img {
    width: 100%;
    height: 100%;
    object-fit: cover;
  }
`;

const Description = styled.div`
  color: white;
  padding: 5%;
  box-sizing: border-box;
  h1 {
    font-size: 2rem;
  }
`;

const Price = styled.div`
  color: green;
  font-size: 2rem;
  display: flex;
  flex-direction: column;
  align-items: center;
  justify-content: space-around;

  div {
    height: 60px;
    display: grid;
    grid-template-columns: 5fr 5fr;
    grid-gap: 20px;
    padding: 0 5%;
    select, option {
      font-size: 1rem;
      text-align: center;
      border: none;
      border-radius: 10px;
      padding: 5%;
      box-sizing: border-box;
      height: 100%;
      width: 100%;
    }
    button {
      font-size: 1.1rem;
      height: 100%;
      width: 100%;
      background-color: green;
      color: white;
      border: none;
      border-radius: 10px;
    }
  }
`;

interface Props {
  image: any;
  title: string;
  emailprof: string;
  price: number;
}

export default function ProductCard({ image, title, emailprof, price }: Props) {
  const history = useHistory()
  const [horario, setHorario] = useState('')

  return (
    <SCProductCard>
      <img src={image} alt={title} />
      <Description>
        <h1>{title}</h1>
        <p id='emailprof'>{emailprof}</p>
      </Description>
      <Price>
        <p>Hora-aula: R${price}</p>
        <div>
          <select id="horario" onChange={(event) => {setHorario(event.target.value)}}>
            <option value="0">00:00 am</option>
            <option value="1">1:00 am</option>
            <option value="2">2:00 am</option>
            <option value="3">3:00 am</option>
            <option value="4">4:00 am</option>
            <option value="5">5:00 am</option>
            <option value="6">6:00 am</option>
            <option value="7">7:00 am</option>
            <option value="8">8:00 am</option>
            <option value="9">9:00 am</option>
            <option value="10">10:00 am</option>
            <option value="11">11:00 am</option>
            <option value="12">12:00 pm</option>
            <option value="13">1:00 pm</option>
            <option value="14">2:00 pm</option>
            <option value="15">3:00 pm</option>
            <option value="16">4:00 pm</option>
            <option value="17">5:00 pm</option>
            <option value="18">6:00 pm</option>
            <option value="19">7:00 pm</option>
            <option value="20">8:00 pm</option>
            <option value="21">9:00 pm</option>
            <option value="22">10:00 pm</option>
            <option value="23">11:00 pm</option>
          </select>
          <button onClick={() => 
            {if(window.confirm('Deseja confirmar o envio da reserva?'))
            {history.push("/ordered");
            {HttpService.post("student", 
            { "nome":window.localStorage.getItem("nome"),
              "email":window.localStorage.getItem("email"),
              "regiao":window.localStorage.getItem("regiao"),
              "modalidade":window.localStorage.getItem("modalidade"),
              "horario":window.localStorage.getItem("horario"),
              "emailprof":emailprof  
            }).then(result => { "Success!" }) } 
            }
            }
            }>Enviar reserva de horário</button>
        </div>
      </Price>
    </SCProductCard>
  );
}
